  || defined(_MSC_VER) || defined (__BORLANDC__) || defined (__TURBOC__)
#define get16bits(d) (*((const uint16_t *) (d)))
#endif

#if !defined (get16bits)
#define get16bits(d) ((((uint32_t)(((const uint8_t *)(d))[1])) << 8)\
                       +(uint32_t)(((const uint8_t *)(d))[0]) )
#endif

using namespace std;

__global__  void  SuperFastHash (const char * data, int len,unsigned int*hash_value) {
unsigned int hash = len, tmp;
int rem;

    if (len <= 0 || data == '\0') *hash_value = 0;

    rem = len & 3;
    len >>= 2;

    /* Main loop */
    for (;len > 0; len--) {
        hash  += get16bits (data);
        tmp    = (get16bits (data+2) << 11) ^ hash;
        hash   = (hash << 16) ^ tmp;
        data  += 2*sizeof (uint16_t);
        hash  += hash >> 11;
    }

    /* Handle end cases */
    switch (rem) {
        case 3: hash += get16bits (data);
                hash ^= hash << 16;
                hash ^= data[sizeof (uint16_t)] << 18;
                hash += hash >> 11;
                break;
        case 2: hash += get16bits (data);
                hash ^= hash << 11;
                hash += hash >> 17;
                break;
        case 1: hash += *data;
                hash ^= hash << 10;
                hash += hash >> 1;
    }

    /* Force "avalanching" of final 127 bits */
    hash ^= hash << 3;
    hash += hash >> 5;
    hash ^= hash << 4;
    hash += hash >> 17;
    hash ^= hash << 25;
    hash += hash >> 6;

    *hash_value = hash;  //return hash
}

int RabinKarpMatch(char *T,char *P)
{
     unsigned int i,j,p,t,n,m,h,found;
	 unsigned int hash_value;
     n = strlen(T);
     m = strlen(P);
     //h = mod(d,m-1,q);
     p = t = 0;
     dim3 dimGrid(1);
    dim3 dimBlock(1);
    
    SuperFastHash<<< dimGrid, dimBlock>>>(P,m,&hash_value); 
          p = hash_value;                        //        (d*p + tonum(P[i])) % q;
	SuperFastHash<<< dimGrid, dimBlock>>>(T,m,&hash_value);
          t = hash_value;                             //(d*t + tonum(T[i])) % q;
     
     for (i=0; i<=n-m; i++)
     {
          if (p == t)
          {
               found = 1;
               for (j=0; j<m; j++)
                   if (P[j] != T[i+j])
                   {
                       found = 0;
                       break;
                   }
               if (found) return i;
          } else {
          if(T+i+1)
		  {
		       SuperFastHash<<< dimGrid, dimBlock>>>(T+i+1,m,&hash_value);
               t = hash_value;                             //(d*t + tonum(T[i])) 
		  }
             //  cout<<t <<endl;
          }
     }
     return -1;
}
 
 





